// page init
jQuery(function(){
	initAnchors();
	initCarousel();
	initSlideShow();
	initOpenClose();
	initSameHeight();
	initCustomHover();
	initFitVids();
	initLoadMore();
	initMultiColumns();
	jQuery('input, textarea').placeholder();
});

// load more init
function initLoadMore() {
	jQuery('.load-more-holder').loadMore({
		linkSelector: 'a.load-more',
		newContentTarget: '.content-area',
		scroll: true,
		additionBottomOffset: 50
	});
}
// split content into columns
function initMultiColumns() {
	jQuery('.text-cols').columnize({
		columns: 2
	});
}
