// scroll gallery init
function initCarousel() {
	jQuery('.carousel').scrollGallery({
		mask: '.mask',
		slider: '.slideset',
		slides: '.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		stretchSlideToMask: true,
		maskAutoSize: true,
		autoRotation: false,
		switchTime: 3000,
		animSpeed: 500,
		step: 1
	});
}

// fade gallery init
function initSlideShow() {
	jQuery('.slideshow').fadeGallery({
		slides: 'div.slide',
		btnPrev: 'a.btn-prev',
		btnNext: 'a.btn-next',
		pagerLinks: '.pagination li',
		event: 'click',
		useSwipe: true,
		autoRotation: true,
		autoHeight: true,
		switchTime: 3000,
		animSpeed: 500
	});
}

// open-close init
function initOpenClose() {
	jQuery('.login-area').openClose({
		hideOnClickOutside: true,
		activeClass: 'active',
		opener: '> .btn-primary',
		slider: '.box-login',
		animSpeed: 400,
		effect: 'slide'
	});
}

// initialize smooth anchor links
function initAnchors() {
	new SmoothScroll({
		anchorLinks: 'a[href^="#"]:not([href="/#"])',
		activeClasses: 'link'
	});
}

// align blocks height
function initSameHeight() {
	jQuery('.footer-top').sameHeight({
		elements: '.col-sm-3',
		flexible: true,
		multiLine: true
	});
	jQuery('.services-blocks').sameHeight({
		elements: '.block',
		flexible: true,
		multiLine: true
	});
	jQuery('.section-professionals .professionals-list').sameHeight({
		elements: '.article .holder',
		flexible: true,
		multiLine: true
	});
	jQuery('.step-details').sameHeight({
		elements: '.img-area',
		flexible: true,
		multiLine: true,
		biggestHeight: true
	});
	jQuery('.section-professionals .professionals-list').sameHeight({
		elements: '.article .heading',
		flexible: true,
		multiLine: true
	});
	jQuery('.carousel.making').sameHeight({
		elements: '.slide-img, .controller',
		multiLine: true,
		flexible: true,
		biggestHeight: true
	});
}

// add classes on hover/touch
function initCustomHover() {
	jQuery('.item-points li a').touchHover();
}

// handle flexible video size
function initFitVids() {
	jQuery('#content').fitVids();
}

